mapper.update:
update data_file_annotation set prediction = #{prediction} where version_file_id = #{versionFileId} and
    label_id in

    #{id}

mapper.delete:
delete from data_file_annotation where version_file_id = #{versionFileId} and
                                       label_id in

    #{labelId}

mapper.select:
select label_id
from data_file_annotation
where version_file_id = #{versionFileId}
